const b = $json.body || $json;
const email = b.email || b.from_email;
const threadId = b.thread_id || b.threadId || "unknown";
const msgId = b.message_id || b.messageId || "unknown";
const eventType = b.event_type || "EMAIL_REPLIED";
return [{
  json: {
    schema_version: 1,
    tenant_id: $env.AG_TENANT_ID,
    occurred_at: new Date().toISOString(),
    event_type: eventType,
    source: "gmail",
    idempotency_key: b.idempotency_key || `gmail:msg:${msgId}:${eventType}`,
    channel: b.channel || "COLD_EMAIL",
    lead: email ? { match: { email } } : undefined,
    payload: { ...b, thread_id: threadId, message_id: msgId }
  }
}];

const crypto = require('crypto');

function stableStringify(value) {
  return JSON.stringify(canonicalize(value));
}
function canonicalize(value) {
  if (value === null) return null;
  const t = typeof value;
  if (t === 'number' || t === 'string' || t === 'boolean') return value;
  if (Array.isArray(value)) return value.map(canonicalize);
  if (t === 'object') {
    const obj = value;
    const out = {};
    for (const k of Object.keys(obj).sort()) {
      const v = obj[k];
      if (v === undefined) continue;
      out[k] = canonicalize(v);
    }
    return out;
  }
  return null;
}

const secret = $env.AG_WEBHOOK_SECRET;
if (!secret) throw new Error('Missing AG_WEBHOOK_SECRET in n8n env');

const event = $json;
const ts = Date.now().toString();
const canonical = stableStringify(event);
const sig = crypto.createHmac('sha256', secret).update(`${ts}.${canonical}`).digest('hex');

return [{ json: { ts, sig, event } }];
